mapper.select:
SELECT
o.id id, o.bonus_url bonusUrl, t.province province, t.city city, ct.merger_short_name mergerShortName,
t.task_name taskName, t.task_number taskNumber, t.property_company propertyCompany, t.bonus bonus,
t.docking_person dockingPerson, u.apply_weixin applyWeixin, u.apply_phone applyPhone, o.apply_time applyTime,
o.complete_time completeTime, o.status, o.balance_bonus balanceBonus
FROM apply_order o, task t, apply_user u, sale_area ct

    o.task_id = t.id AND o.apply_user_id = u.id AND ct.id=t.city

        AND t.province=#{province}



        AND t.supplier_id=#{userGroup}



        AND t.city=#{city}



        AND t.task_number LIKE '%' #{taskNum} '%'



        AND o.status=#{status}



        AND t.task_name=#{taskName}



        AND t.property_company LIKE '%' #{propertyCompany} '%'



        AND u.apply_phone LIKE '%' #{applyPhone} '%'



        AND o.apply_time BETWEEN #{start} AND #{end}


ORDER BY o.apply_time DESC

    limit #{fromIndex}, #{size}

SELECT o.id id, t.province province, t.city city, t.task_name taskName, t.task_number taskNumber, t.property_company propertyCompany, t.bonus bonus, o.status, o.balance_bonus balanceBonus FROM apply_order o, task t, apply_user u
o.task_id = t.id AND o.apply_user_id = u.id AND u.apply_weixin=#{applyWeiXin}
ORDER BY o.apply_time DESC

SELECT
COUNT(*)
FROM apply_order o, task t, apply_user u

    o.task_id = t.id AND o.apply_user_id = u.id

        AND t.province=#{province}



        AND t.supplier_id=#{userGroup}



        AND t.city=#{city}



        AND t.task_number LIKE '%' #{taskNum} '%'



        AND o.status=#{status}



        AND t.task_name=#{taskName}



        AND t.property_company LIKE '%' #{propertyCompany} '%'



        AND u.apply_phone LIKE '%' #{applyPhone} '%'



        AND o.apply_time BETWEEN #{start} AND #{end}

mapper.update:

update apply_order
set
status = #{status,jdbcType=VARCHAR},
remarks = #{remarks,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}


update apply_order
set
balance_bonus = #{balanceBonus,jdbcType=DECIMAL},
bonus_url = #{bonusUrl,jdbcType=VARCHAR},
complete_time=#{completeTime, jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
